import React, { useEffect, useState } from "react";
import { useMoralis, useMoralisQuery } from "react-moralis";
import { getNativeByChain } from "helpers/networks";
import { Card, Image, Tooltip, Modal, Badge, Skeleton, Alert } from "antd";
import {
  FileSearchOutlined,
  RightCircleOutlined,
  // SendOutlined,
  ShoppingCartOutlined,
} from "@ant-design/icons";
import { getExplorer } from "helpers/networks";
import AddressInput from "./AddressInput";
import { useVerifyMetadata } from "hooks/useVerifyMetadata";
import { useNFTTokenIds } from "hooks/useNFTTokenIds";
import { getCollectionsByChain } from "helpers/collections";
import { useWeb3ExecuteFunction } from "react-moralis";
import { MarketAddress, ABI } from "contracts/contract";
import Account from "./Account/Account";
import AccountMM from "./Account/AccountMM";

const { Meta } = Card;

const styles = {
  NFTs: {
    display: "flex",
    flexWrap: "wrap",
    WebkitBoxPack: "start",
    justifyContent: "flex-start",
    margin: "0 auto",
    maxWidth: "1000px",
    width: "100%",
    gap: "10px",
  },
};

function NFTTokenIds({ address, setContractAddress }) {
  const { NFTTokenIds } = useNFTTokenIds(address);
  const {
    Moralis,
    chainId,
    walletAddress,
    isWeb3Enabled,
    enableWeb3,
    isAuthenticated,
    isWeb3EnableLoading,
  } = useMoralis();
  const [visible, setVisibility] = useState(false);
  const nativeName = getNativeByChain(chainId);
  const contractProcessor = useWeb3ExecuteFunction();
  const contractABIJson = JSON.parse(ABI);
  const purchaseItemFunction = "createMarketSale";
  const { verifyMetadata } = useVerifyMetadata();
  const NFTCollections = getCollectionsByChain(chainId);
  const [nftToBuy, setNftToBuy] = useState();
  const queryMarketItems = useMoralisQuery("CreatedMarketItems");
  const fetchMarketItems = JSON.parse(
    JSON.stringify(queryMarketItems.data, [
      "objectId",
      "createdAt",
      "price",
      "nftContract",
      "itemId",
      "sold",
      "tokenId",
      "seller",
      "owner",
      "confirmed",
    ]),
  );

  console.log(fetchMarketItems);

  const purchase = async () => {
    const tokenDetails = getMarketItem(nftToBuy);
    const itemID = tokenDetails.itemId;
    const tokenPrice = tokenDetails.price;
    const ops = {
      contractAddress: MarketAddress,
      functionName: purchaseItemFunction,
      abi: contractABIJson,
      params: {
        nftContract: nftToBuy.token_address,
        itemId: itemID,
      },
      msgValue: tokenPrice,
    };

    await contractProcessor.fetch({
      params: ops,
      onSuccess: () => {
        alert("Bought this NFT");
        updateSoldMarketItem();
      },
      onError: (error) => {
        alert(error);
      },
    });
  };

  const handleBuyClick = (nft) => {
    setNftToBuy(nft);
    setVisibility(true);
  };

  const updateSoldMarketItem = async () => {
    const id = getMarketItem(nftToBuy).objectId;
    const marketList = Moralis.Object.extend("CreatedMarketItems");
    const query = new Moralis.Query(marketList);
    await query.get(id).then((obj) => {
      obj.set("sold", true);
      obj.set("owner", walletAddress);
      obj.save();
    });
  };

  const getMarketItem = (nft) => {
    const result = fetchMarketItems?.find(
      (e) =>
        e.nftContract === nft?.token_address &&
        e.tokenId === nft?.token_id &&
        e.sold === false &&
        e.confirmed === true,
    );
    return result;
  };

  useEffect(() => {
    const connectorId = window.localStorage.getItem("connectorId");
    if (isAuthenticated && !isWeb3Enabled && !isWeb3EnableLoading)
      enableWeb3({ provider: connectorId });
  }, [isAuthenticated, isWeb3Enabled]);

  if (!isAuthenticated) {
    return (
      <div className="flex w-full h-[90vh] items-center p-4 border-b border-solid border-gray-800">
        <div className="flex flex-col items-center mx-auto">
          <h1 className="text-white text-3xl text-center font-medium leading-normal">
            Sign in
          </h1>
          <p className="text-lg text-center w-4/5">
            Please sign in with your blockchain wallet to see this page
          </p>
          <AccountMM />
        </div>
      </div>
    );
  } else {
    return (
      <div className="flex flex-col w-full items-center p-4 border-b border-solid border-gray-800">
        <div className="flex flex-col  mt-20 w-full p-4">
          <h1 className="text-white text-5xl text-left font-bold leading-normal ">
            Explore NFTs
          </h1>
        </div>
        <div className="p-6 flex items-center flex-wrap gap-10 justify-start max-w-[1200px] w-full">
          {NFTTokenIds &&
            !address !== "explore" &&
            NFTTokenIds.map((nft, index) => {
              //Verify Metadata
              nft = verifyMetadata(nft);
              return (
                <Card
                  hoverable
                  actions={[
                    <Tooltip title="View On Blockexplorer">
                      <FileSearchOutlined
                        onClick={() =>
                          window.open(
                            `${getExplorer(chainId)}address/${
                              nft.token_address
                            }`,
                            "_blank",
                          )
                        }
                      />
                    </Tooltip>,

                    <Tooltip title="Buy This NFT">
                      <ShoppingCartOutlined
                        onClick={() => handleBuyClick(nft)}
                      />
                    </Tooltip>,
                  ]}
                  style={{ width: 240, border: "2px solid #e7eaf3" }}
                  cover={
                    <Image
                      preview={false}
                      src={nft?.image || "error"}
                      fallback="data:image/png;base64,"
                      alt=""
                      style={{ height: "300px" }}
                    />
                  }
                  key={index}
                >
                  {getMarketItem(nft) && (
                    <Badge.Ribbon text="Buy Now" color="green"></Badge.Ribbon>
                  )}
                  <Meta title={nft.name} description={`# ${nft.token_id}`} />
                </Card>
              );
            })}

          {NFTCollections &&
            address === "explore" &&
            NFTCollections.map((nft, index) => {
              //Verify Metadata
              return (
                <Card
                  hoverable
                  actions={[
                    <Tooltip title="View This Collection">
                      <RightCircleOutlined
                        onClick={() => setContractAddress(nft.addrs)}
                      />
                    </Tooltip>,
                  ]}
                  style={{ width: 240, border: "2px solid #e7eaf3" }}
                  cover={
                    <Image
                      preview={false}
                      src={nft?.image || "error"}
                      fallback="data:image/png;base64,"
                      alt=""
                      style={{ height: "300px" }}
                    />
                  }
                  key={index}
                >
                  <Meta title={nft.name} />
                </Card>
              );
            })}
        </div>
        {getMarketItem(nftToBuy) ? (
          <Modal
            title={`Buy ${nftToBuy?.name || "NFT"}`}
            visible={visible}
            onCancel={() => setVisibility(false)}
            onOk={() => purchase()}
            okText="Buy"
          >
            <div style={{ width: "250px", margin: "auto" }}>
              <Badge.Ribbon
                text={`${
                  getMarketItem(nftToBuy).price / ("1e" + 18)
                } ${nativeName}`}
                color="green"
              >
                <img
                  src={nftToBuy?.image}
                  style={{
                    width: "250px",
                    margin: "auto",
                    borderRadius: "10px",
                    marginBottom: "15px",
                  }}
                />
              </Badge.Ribbon>
            </div>
          </Modal>
        ) : (
          <Modal
            title={`Buy ${nftToBuy?.name || "NFT"}`}
            visible={visible}
            onCancel={() => setVisibility(false)}
            onOk={() => setVisibility(false)}
            okText="Ok"
          >
            <img
              src={nftToBuy?.image}
              style={{
                width: "250px",
                margin: "auto",
                borderRadius: "10px",
                marginBottom: "15px",
              }}
            />
            <Alert message="This NFT is not for sale" type="warning" />
          </Modal>
        )}
      </div>
    );
  }
}

export default NFTTokenIds;
